import { useEffect, useState } from "react";
import { fetchWords } from "./api";

function Header() {
    return (
        <header className="hero is-dark is-bold">
            <div className="hero-body">
                <div className="container">
                    <h1 className="title">名言API</h1>
                </div>
            </div>
        </header>
    );
}

function Loading() {
    return <p>Lodading...</p>
}

function Button() {
    return (
        <div id="btn" className="control">
            <button type="submit" className="button is-dark">
                Reload
            </button>
        </div>
    );
}

function Form(props) {
    function handleSubmit(event) {
        event.preventDefault();
        const { breed } = event.target.elements;
        props.onFormSubmit();
    }

    return (
        <div>
            <p>document.write("Samurai")</p>
            <form onSubmit={handleSubmit}>
                <div className="field has-addons">
                    <div className="control">
                        <button type="submit" className="button is-dark">
                        Reload
                        </button>
                    </div>
                </div>
            </form>
        </div>
    );
}

function Print(props) {
    const {msg} = props
    return (
        <text src={msg}></text>
    );
}

function Main() {
    const [msg, setText] = useState(null);
    useEffect(() => {
        fetchWords().then((msg) => {
          setText(msg)
        });
    }, []);
    function reloadWord() {
        fetchWords().then((msg) => {
            setText(msg);
        });
    }

    return (
        <main>
            <section className="section">
                <div className="container">
                <Form onFormSubmit={reloadWord}/>
                </div>
            </section>
            <div>
                <Print msg={msg} />
                <Button />
            </div>
        </main>
    );
}

function App() {
    return (
        <div>
            <Header />
            <Main />
        </div>
    );
}

export default App;
